const apiKey = "e2cf3ef830ea82bf925fc3d6eed026a5";
const apiUrl = "https://api.openweathermap.org/data/2.5/forecast?units=metric&q=";

const searchBox = document.querySelector(".search input");
const searchBtn = document.querySelector(".search button");
const oneDayBtn = document.querySelector(".toggle_button:nth-child(1)");
const twoDaysBtn = document.querySelector(".toggle_button:nth-child(2)");
const ctx = document.getElementById('myChart');
const dateInput = document.querySelector(".date")
const weatherIcon = document.querySelector(".weather-icon")

var data_temp = []

async function checkWeather(city, days = 1) {
    const response = await fetch(apiUrl + city + `&appid=${apiKey}`);
    
    if(response.status == 404) {
        document.querySelector(".error").style.display = "block";
        document.querySelector(".weather").style.display = "none";
    } else{
        var data = await response.json();

        console.log(data);

        if(days === 1) {
            document.querySelector(".city").innerHTML = data.city.name;
            document.querySelector(".temp").innerHTML = Math.round(data.list[1].main.temp) + "°C";
            document.querySelector(".humidity").innerHTML = data.list[1].main.humidity + "%";
            document.querySelector(".wind").innerHTML = data.list[1].wind.speed + " km/h ";

            if(data.list[1].weather[0].main == "Clouds") {
                weatherIcon.src = "data:image/png;base64,";
            } 
            else if(data.list[1].weather[0].main == "Clear") {
                weatherIcon.src = "data:image/png;base64,";
            } 
            else if(data.list[1].weather[0].main == "Rain") {
                weatherIcon.src = "data:image/png;base64,";
            } 
            else if(data.list[1].weather[0].main == "Drizzle") {
                weatherIcon.src = "data:image/png;base64,";
            } 
            else if(data.list[1].weather[0].main == "Mist") {
                weatherIcon.src = "data:image/png;base64,";
            } 

            data_temp = [data.list[2].main.temp, data.list[3].main.temp, data.list[4].main.temp, data.list[5].main.temp, data.list[6].main.temp, data.list[7].main.temp,data.list[8].main.temp]

            createChart();
        }  

        else if (days === 2) {
            document.querySelector(".city").innerHTML = data.city.name;
            document.querySelector(".temp").innerHTML = Math.round(data.list[9].main.temp) + "°C";
            document.querySelector(".humidity").innerHTML = data.list[1].main.humidity + "%";
            document.querySelector(".wind").innerHTML = data.list[1].wind.speed + " km/h ";

            if(data.list[9].weather[9].main == "Clouds") {
                weatherIcon.src = "data:image/png;base64,";
            } 
            else if(data.list[9].weather[9].main == "Clear") {
                weatherIcon.src = "data:image/png;base64,";
            } 
            else if(data.list[9].weather[9].main == "Rain") {
                weatherIcon.src = "data:image/png;base64,";
            } 
            else if(data.list[9].weather[9].main == "Drizzle") {
                weatherIcon.src = "data:image/png;base64,";
            } 
            else if(data.list[9].weather[9].main == "Mist") {
                weatherIcon.src = "data:image/png;base64,";
            } 
            data_temp = [data.list[10].main.temp, data.list[11].main.temp, data.list[12].main.temp, data.list[13].main.temp, data.list[14].main.temp, data.list[15].main.temp,data.list[16].main.temp]

            createChart();
        }


        document.querySelector(".weather").style.display = "block";
        document.querySelector(".error").style.display = "none";
    }
    
}

function createChart() {
    new Chart(ctx, {
    type: 'line',
    data: {
        labels: ['03:00','6:00', '9:00', '12:00', '15:00','18:00', '21:00'],
        datasets: [{
        label: 'temperature',
        data: data_temp,
        borderWidth: 1
        }]
    },
    options: {
        
        scales: {
        y: {
            beginAtZero: true
        },
        x: {
            display: true,
            title: "Day",
        }
        }
    }
    });
}

searchBtn.addEventListener("click", ()=> {
    checkWeather(searchBox.value);
})

oneDayBtn.addEventListener("click", ()=> {
    checkWeather(searchBox.value, 1);
})

twoDaysBtn.addEventListener("click", ()=> {
    checkWeather(searchBox.value, 2);
})